/* ===== Inputs from ADF Script parameters ===== Expect a Script parameter named p_batch_id (Int32, Input) */
DECLARE @BatchId INT = @p_batch_id;

/* ===== Safer transaction behavior ===== */
SET XACT_ABORT ON;

DECLARE @today_ist  datetime2(3) = SYSDATETIMEOFFSET() AT TIME ZONE 'India Standard Time';
DECLARE @today_date date         = CAST(@today_ist AS date);

DECLARE @WatermarkId INT;
SELECT TOP 1 @WatermarkId = WATERMARK_ID
FROM dbo.WATERMARK_TRACKER
WHERE PIPELINE_NAME = 'P_SCD_T2_Inc_Vivid_Customer'
  AND SOURCE_TABLE_NAME = 'BLOB_CUSTOMER_RAW'
ORDER BY UPDATED_AT_TSTMP DESC;

IF @WatermarkId IS NULL SET @WatermarkId = 0;  -- fallback if nothing found

DECLARE @chg TABLE (action nvarchar(10));

BEGIN TRY
    BEGIN TRAN;

    /* 1) Expire current rows whose attributes changed (close them on TODAY) */
    MERGE DWH.DIM_CUSTOMER_DETAILS AS T
    USING (SELECT * FROM DWH.STG_CUSTOMER_DETAILS) AS S
      ON  T.INDEX_NUM    = S.INDEX_NUM
      AND T.CUSTOMER_ID  = S.CUSTOMER_ID
      AND T.ROW_EXPIR_DT = '9999-12-31'
    WHEN MATCHED AND ISNULL(S.HASH_DIFF, 0x) <> ISNULL(T.HASH_DIFF, 0x)
      THEN UPDATE SET
           T.ROW_EXPIR_DT       = @today_date,       -- close on today (not yesterday)
           T.LST_UPD_TSTMP_IST  = @today_ist
      OUTPUT $action INTO @chg;

    /* 2) Insert new versions for the keys just expired in step 1
          (new rows get ROW_EFF_DT default = today; VERSION_NUM = prev+1) */
    INSERT INTO DWH.DIM_CUSTOMER_DETAILS
    (
      INDEX_NUM, CUSTOMER_ID,
      FIRST_NAME, LAST_NAME, COMPANY, CITY, COUNTRY,
      PHONE_1, PHONE_2, EMAIL, SUBSCRIPTION_DATE, WEBSITE, SRC_ROW_INSRT_TSTMP,
      HASH_DIFF, VERSION_NUM,
      WATERMARK_ID, BATCH_ID
    )
    SELECT
      S.INDEX_NUM, S.CUSTOMER_ID,
      S.FIRST_NAME, S.LAST_NAME, S.COMPANY, S.CITY, S.COUNTRY,
      S.PHONE_1, S.PHONE_2, S.EMAIL, S.SUBSCRIPTION_DATE, S.WEBSITE, S.SRC_ROW_INSRT_TSTMP,
      S.HASH_DIFF, T.VERSION_NUM + 1,
      @WatermarkId, @BatchId
    FROM DWH.STG_CUSTOMER_DETAILS S
    JOIN DWH.DIM_CUSTOMER_DETAILS T
      ON T.INDEX_NUM   = S.INDEX_NUM
     AND T.CUSTOMER_ID = S.CUSTOMER_ID
    WHERE T.ROW_EXPIR_DT = @today_date;   -- rows just closed above

    /* 3) Insert brand-new keys (first versions) */
    MERGE DWH.DIM_CUSTOMER_DETAILS AS T
    USING (SELECT * FROM DWH.STG_CUSTOMER_DETAILS) AS S
      ON  T.INDEX_NUM   = S.INDEX_NUM
      AND T.CUSTOMER_ID = S.CUSTOMER_ID
    WHEN NOT MATCHED BY TARGET
      THEN INSERT
      (
        INDEX_NUM, CUSTOMER_ID,
        FIRST_NAME, LAST_NAME, COMPANY, CITY, COUNTRY,
        PHONE_1, PHONE_2, EMAIL, SUBSCRIPTION_DATE, WEBSITE, SRC_ROW_INSRT_TSTMP,
        HASH_DIFF, VERSION_NUM,
        WATERMARK_ID, BATCH_ID
      )
      VALUES
      (
        S.INDEX_NUM, S.CUSTOMER_ID,
        S.FIRST_NAME, S.LAST_NAME, S.COMPANY, S.CITY, S.COUNTRY,
        S.PHONE_1, S.PHONE_2, S.EMAIL, S.SUBSCRIPTION_DATE, S.WEBSITE, S.SRC_ROW_INSRT_TSTMP,
        S.HASH_DIFF, 1,
        @WatermarkId, @BatchId
      )
      OUTPUT $action INTO @chg;

    COMMIT;
END TRY
BEGIN CATCH
    IF @@TRANCOUNT > 0 ROLLBACK;
    THROW;
END CATCH;

/* ===== Return counts back to ADF ===== */
SELECT
  SUM(CASE WHEN action = 'INSERT' THEN 1 ELSE 0 END) AS RowsInserted,
  SUM(CASE WHEN action = 'UPDATE' THEN 1 ELSE 0 END) AS RowsExpired
FROM @chg;

SET XACT_ABORT ON;

DECLARE @today_ist  datetime2(3) = SYSDATETIMEOFFSET() AT TIME ZONE 'India Standard Time';
DECLARE @today_date date         = CAST(@today_ist AS date);

BEGIN TRY
  BEGIN TRAN;

  ;WITH missing AS (
    SELECT D.INDEX_NUM, D.CUSTOMER_ID
    FROM DWH.DIM_CUSTOMER_DETAILS D
    WHERE D.ROW_EXPIR_DT = '9999-12-31'              -- only current rows
  EXCEPT
  SELECT K.INDEX_NUM, K.BUSSINESS_ID
  FROM DWH.DIM_SRC_KEYS K
  WHERE BUSSINESS_ID_COL_NM = 'CUSTOMER_ID'
  )
  UPDATE D
     SET D.ROW_EXPIR_DT       = @today_date,         -- expire today (not yesterday)
         D.ROW_STS_CD         = 'D',
         D.LST_UPD_TSTMP_IST  = @today_ist
  FROM DWH.DIM_CUSTOMER_DETAILS D
  JOIN missing M
    ON D.INDEX_NUM   = M.INDEX_NUM
   AND D.CUSTOMER_ID = M.CUSTOMER_ID
  WHERE D.ROW_EXPIR_DT = '9999-12-31';

  COMMIT;
END TRY
BEGIN CATCH
  IF @@TRANCOUNT > 0 ROLLBACK;
  THROW;
END CATCH;

-- Return count to ADF
SELECT @@ROWCOUNT AS RowsDeleted;

